    <script src="https://www.youtube.com/iframe_api"></script>
    <script>


        // Variables globales
        var player;
        var videoId = '2DFf7wTxqVs'; // Remplacez par l'ID de la vidéo YouTube

        let taille = '460';
        if (window.matchMedia("(min-width: 768px)").matches) {
            taille = '460'; // Valeur pour un grand écran
        } else {
            taille = '320'; // Valeur pour un smartphone
        }
        // Cette fonction est appelée lorsque l'API YouTube Player est prête
        function onYouTubeIframeAPIReady() {

            player = new YT.Player('player', {
                height: taille,
                width: '1040',
                videoId: videoId,
                playerVars: {
                    'autoplay': 0, // Définir sur 1 pour l'autoplay
                    'controls': 1, // Afficher les contrôles de la vidéo
                    'rel': 0, // Ne pas afficher de vidéos similaires à la fin
                },
                events: {
                    'onReady': onPlayerReady
                }
            });
        }

        // Cette fonction est appelée lorsque le lecteur est prêt
        function onPlayerReady(event) {
            // Ajoute un écouteur de clic sur la miniature pour lire la vidéo
            var thumbnail = document.getElementById('thumbnail');
            thumbnail.addEventListener('click', function() {
                event.target.playVideo();
                thumbnail.style.display = 'none'; // Masque la miniature après le clic
            });
        }
        onYouTubeIframeAPIReady();
    </script>

@endsection
